/* === Кнопка ImportJS. Сделано HIHRAIM'ом из AdminDashboard JS-Button === */
(function() {
    if (
        mw.config.get('wgCanonicalSpecialPageName') !== 'AdminDashboard' ||
        window.AdminDashboardJSButtonLoaded
    ) {
        return;
    }
    window.AdminDashboardImportJSButtonLoaded = true;
    var AdminDashboardJSButton = {
        init: function(i18n) {
            console.log(i18n);
            this.$control = $('<li>', {
                'class': 'control',
                'data-tooltip': ('Управляйте импортом скриптов из Dev Wiki.')
            }).append(
                $('<a>', {
                    'class': 'set',
                    href: mw.util.getUrl('MediaWiki:ImportJS', { action: 'edit' })
                }).append(
                    $('<span>', {
                        'class': 'representation'
                    }).append(
                        $('<span>', {
                            text: 'IJS'
                        })
                    ),
                    i18n.msg('text').plain()
                )
            ).hover($.proxy(this.hover, this), $.proxy(this.unhover, this));
            $('.control a[data-tracking="special-css"]').parent().after(this.$control);
            this.$tooltip = $('.control-section.wiki > header > .dashboard-tooltip');
        },
        hover: function(e) {
            this.$tooltip.text(this.$control.data('tooltip'));
        },
        unhover: function(e) {
            this.$tooltip.text('');
        },
        hook: function(i18n) {
            i18n.loadMessages('AdminDashboard_JS-Button')
                .then($.proxy(this.init, this));
        }
    };
    mw.hook('dev.i18n').add($.proxy(AdminDashboardJSButton.hook, AdminDashboardJSButton));
    importArticle(
        {
            type: 'script',
            article: 'u:dev:MediaWiki:I18n-js/code.js'
        },
        {
            type: 'style',
            article: 'u:dev:MediaWiki:AdminDashboardJSButton.css'
        }
    );
})();

/* == Дополнительное == */
/* === Популярные страницы. Сделано HIHRAIM'ом из TopArticles === */
(function() {
    'use strict';
    var results, i18n;

    function searchMap(article) {
        var description = article.abstract;
        if (description.indexOf(article.title) === 0) {
            description = description.slice(article.title.length);
        } else {
            description = ' - ' + description;
        }
        return $('<li>', {
            attr: {
                'data-title': article.title,
                'data-id': article.id
            }
        }).append(
            $('<div>', {
                'class': 'TopArticlesThumbnail'
            }).append(
                $('<a>', {
                    href: article.url,
                    title: article.title
                }).append(
                    $('<img>', {
                        src: article.thumbnail
                    })
                )
            ),
            $('<div>', {
                'class': 'TopArticlesDescription'
            }).append(
                $('<a>', {
                    href: article.url,
                    text: article.title
                }),
                $('<span>', {
                    text: description
                })
            )
        );
    }
    function plainMap(article) {
        return $('<li>').append(
            $('<a>', {
                href: article.url,
                text: article.title
            })
        );
    }
    function each(_, el) {
        var $this = $(el),
            data = $this.data(),
            limit = Number(data.limit) || 5,
            isSearch = data.type === 'search',
            namespace = (data.namespace || '0').split(',').map(Number),
            res = results.filter(function(r) {
                return namespace.indexOf(r.ns) !== -1 &&
                       (!isSearch || r.thumbnail);
            }).slice(0, limit);
        if (isSearch) {
            $this.html(
                $('<section>', {
                    'class': 'TopArticles TopArticlesSearch'
                }).append(
                    $('<h2>', {
                        text: i18n.msg('top-articles').plain()
                    }),
                    $('<ul>', {
                        append: res.map(searchMap)
                    })
                )
            );
        } else {
            $this.html(
                $('<ul>', {
                    'class': 'TopArticles TopArticlesPlain',
                    append: res.map(plainMap)
                })
            );
        }
    }
    function init(i18nd, data) {
        i18n = i18nd;
        results = data[0].items;
        mw.hook('wikipage.content').add(function($content) {
            $content
                .find('#top-articles:not(.loaded), .TopArticles:not(.loaded)')
                .each(each);
        });
    }
    mw.hook('dev.i18n').add(function(i18no) {
        $.when(
            i18no.loadMessages('TopArticles'),
            $.get(mw.config.get('wgScriptPath') + '/api/v1/Articles/Top', {
                expand: 1
            })
        ).then(init);
    });
    importArticle({
        type: 'script',
        article: 'u:dev:MediaWiki:I18n-js/code.js'
    });
    importArticle({
        type: 'style',
        article: 'u:dev:MediaWiki:TopArticles.css'
    });
})();

/* === Предварительный просмотр страницы при наведении на ссылку (изменение для скрипта LinkPreview) === */
/* ==== Изменяет изображение, отображаемое, когда на странице нет изображений (взято с английской Pokemon Wiki) ==== */
window.pPreview = $.extend(
    true,
    window.pPreview,
    {
        RegExp: (window.pPreview || {}).RegExp || {}
    }
);
window.pPreview.defimage = 'https://static.wikia.nocookie.net/pokemon/images/e/e3/No_Image.png';
window.pPreview.noimage = 'https://static.wikia.nocookie.net/pokemon/images/e/e3/No_Image.png';

/**
 * @module              DiscordBanner
 * @description         Add a banner to a wiki's Discord.
 * @author              Americhino
 *                      Unai01
 * @usingcodeby         KockaAdmiralac (AddRailModule)
 * @version             1.1.0
 * @license             CC-BY-SA 3.0
 *
 */
mw.loader.using('mediawiki.api').then(function () {
    var FANDOM_DEVELOPERS_SERVER_ID = '246075715714416641', FANDOM_DEVELOPERS_INVITE_CODE = 'Vgfu9qb';
    var loaded, loadQueue = [],
        $rail = $('#WikiaRail'),
        railClass = $rail.attr('class');
    // Configuration
    var config = window.DiscordBannerSettings || {},
        bannerStyle = config.bannerStyle || '3',
        inviteLink = config.inviteLink || FANDOM_DEVELOPERS_INVITE_CODE,
        prependToRail = config.prependToRail === undefined ? true : config.prependToRail, // Prepends to rail by default
        noRail = config.noRail || false; // Adds rail module by default
    if (
        (!$rail.length || noRail) &&
        $('.discordBanner-variables').length === 0
    ) {
        return;
    }
    function place($module) {
        var $ads;
        if (prependToRail) {
            $ads = $('#top-right-boxad-wrapper, #top-boxad-wrapper, #NATIVE_TABOOLA_RAIL').last();
            if ($ads.length) {
                $module.insertAfter($ads);
            } else {
                $rail.prepend($module);
            }
        } else {
            $ads = $('.rail-sticky-module');
            if ($ads.length) {
                $module.insertBefore($ads);
            } else {
                $rail.append($module);
            }
        }
    }

    function placeQueue() {
        if (noRail) {
            return;
        }
        loaded = true;
        loadQueue.forEach(place);
    }

    function actuallyConstructBanner(skeleton, serverId, inviteCode, bannerStyle) {
        return skeleton.attr('style', 'transform: scale(0.9); margin-left: -2px;').html($('<a>', {
            href: 'https://discord.gg/' + inviteCode,
        }).append($('<img>', {
            src: 'https://discord.com/api/guilds/' + serverId + '/widget.png?style=banner' + bannerStyle,
        }).css('width', '100%')));
    }

    function constructBanner(skeleton, serverId, inviteCode, bannerStyle) {
        return $.Deferred(function (deferred) {
            if (serverId === FANDOM_DEVELOPERS_SERVER_ID || inviteCode !== FANDOM_DEVELOPERS_INVITE_CODE) {
                // There's no configuration mismatch.
                deferred.resolve(actuallyConstructBanner(skeleton, serverId, inviteCode, bannerStyle));
                return;
            }
            // Server ID isn't Fandom Developers, but invite code _is_. If the current user is an admin/WM/staff/helper,
            // let's complain to them. Otherwise, we'll be nice and try to get an instant invite code
            // from `widget.json`.
            if (/sysop|wiki-representative|wiki-specialist|staff|helper/.test(mw.config.get('wgUserGroups'))) {
                deferred.resolve(actuallyConstructBanner(skeleton, serverId, inviteCode, bannerStyle).append($('<div>', {
                    style: 'color: #e81a3f;',
                    html: 'Your banner\'s invite link is not configured correctly. For help, ask <a href="https://discord.gg/' + FANDOM_DEVELOPERS_INVITE_CODE + '">here</a>.'
                })));
                return;
            }
            $.get('https://discord.com/api/guilds/' + serverId + '/widget.json').done(function (data) {
                if (!data.instant_invite) {
                    // Widget invite channel is not set, so we just won't show this banner to normal users.
                    deferred.reject();
                    return;
                }
                var instantInviteCode = /[\w-]+$/.exec(data.instant_invite)[0];
                if (!instantInviteCode) {
                    deferred.reject();
                    return;
                }
                deferred.resolve(actuallyConstructBanner(skeleton, serverId, instantInviteCode, bannerStyle));
            }).fail(function () {
                deferred.reject();
            });
        });
    }

    new mw.Api().get({
        action: 'parse',
        prop: 'wikitext',
        page: 'MediaWiki:Custom-DiscordBanner-id',
        format: 'json'
    }).done(function (d) {
        var id;
        if (JSON.stringify(d).indexOf('error') > -1) {
            id = FANDOM_DEVELOPERS_SERVER_ID;
        } else {
            id = d.parse.wikitext['*'];
        }
        $('.discordBanner-variables').each(function () {
            var $placeholder = $(this);
            var templateID = $placeholder.find('.db-id').text();
            var invite = $placeholder.find('.db-invitelink').text();
            if (!templateID || !invite) {
                return;
            }
            var style = $placeholder.find('.db-style').text() || bannerStyle;
            constructBanner($('<div>', { class: 'db-banner' }), templateID, invite, style).done(function ($template) {
                $placeholder.empty().append($template).show();
            });
        });
        if (noRail) {
            return;
        }
        constructBanner($('<section>', { class: 'discordBanner rail-module' }), id, inviteLink, bannerStyle).done(function ($section) {
            if (loaded) {
                place($section);
            } else {
                loadQueue.push($section);
            }
            mw.hook('wikipage.content').fire($section);
        });
    });
    if (railClass) {
        var splCls = railClass.split(/\s+/);
        if (splCls.indexOf('loaded') === -1 && splCls.indexOf('is-ready') === -1) {
            $rail.on('afterLoad.rail', placeQueue);
        } else {
            placeQueue();
        }
    } else {
        placeQueue();
    }
});

